import React, { useState, useEffect } from "react";

function UpdateArea(props) {
  const [note, setNote] = useState({
    title: "",
    content: "",
    author: "",
  });

  useEffect(() => {
    if (props.note) {
      setNote({
        title: props.note.title,
        content: props.note.content,
        author: props.note.author,
      });
    }
  }, [props.note]);

  const formStyle = {
    display: "flex",
    flexDirection: "column",
    gap: "8px",
    maxWidth: "400px",
    margin: "auto",
    padding: "16px",
    borderRadius: "8px",
    backgroundColor: "#f9f9f9",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
  };

  const inputStyle = {
    padding: "8px",
    border: "1px solid #ddd",
    borderRadius: "4px",
    fontSize: "1rem",
  };

  const buttonStyle = {
    padding: "8px",
    backgroundColor: "#FFA500",
    color: "white",
    border: "none",
    borderRadius: "4px",
    fontSize: "1rem",
    cursor: "pointer",
    marginTop: "8px",
  };

  function handleChange(event) {
    const { name, value } = event.target;
    setNote((prevNote) => ({
      ...prevNote,
      [name]: value,
    }));
  }

  function submitNote(event) {
    event.preventDefault();
    props.onEdit(props.note.id, note.title, note.content, note.author);
    setNote({
      title: "",
      content: "",
      author: "",
    });
  }

  return (
    <div>
      <form style={formStyle} onSubmit={submitNote}>
        <input
          name="title"
          onChange={handleChange}
          value={note.title}
          placeholder="Title"
          style={inputStyle}
        />
        <textarea
          name="content"
          onChange={handleChange}
          value={note.content}
          placeholder="Take a note..."
          rows="3"
          style={inputStyle}
        />
        <input
          name="author"
          onChange={handleChange}
          value={note.author}
          placeholder="Author"
          style={inputStyle}
        />
        <button type="submit" style={buttonStyle}>
          Update
        </button>
      </form>
    </div>
  );
}

export default UpdateArea;
