import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Link, Routes } from "react-router-dom";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import NoteForm from "./NoteForm";
import Login from "./Login";
import AccessDenied from "./AccessDenied";
import NoteSearchBar from "./NoteSearchBar";
import About from "./About";
import axios from "axios";

const API_URL = "http://localhost:8000";

function App() {
  const [notes, setNotes] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [wasValid, setWasValid] = useState(true);
  const [selectedNote, setSelectedNote] = useState(null);

  const fetchNotes = async (searchParams = {}) => {
    try {
      const response = await axios.get(`${API_URL}/api/notes/search`, { params: searchParams });
      setNotes(response.data);
    } catch (error) {
      console.error("Error fetching notes:", error);
    }
  };

  useEffect(() => {
    if (isLoggedIn) {
      fetchNotes();
    }
  }, [isLoggedIn]);

  const handleLogin = async (username, password) => {
    try {
      const response = await axios.post(`${API_URL}/api/users/login`, { username, password });
      setCurrentUser(response.data.user);
      setIsLoggedIn(true);
      setWasValid(true);
    } catch (error) {
      console.error("Login failed:", error.response?.data || error.message);
      setIsLoggedIn(false);
      setWasValid(false);
    }
  };

  const addOrUpdateNote = async (noteData) => {
    try {
      const response = await axios.post(`${API_URL}/api/notes`, noteData);
      console.log("Note added/updated:", response.data);
      fetchNotes();
      setSelectedNote(null);
    } catch (error) {
      console.error("Error adding/updating note:", error.response?.data || error.message);
    }
  };

  const deleteNote = async (id) => {
    try {
      await axios.delete(`${API_URL}/api/notes/${id}`);
      fetchNotes();
    } catch (error) {
      console.error("Error deleting note:", error);
    }
  };

  const editNote = (note) => {
    setSelectedNote(note);
  };

  const handleSearch = (searchTerms) => {
    fetchNotes(searchTerms);
  };

  const NotesPage = () => (
    <>
      <NoteSearchBar onSearch={handleSearch} />
      <NoteForm
        onSubmit={addOrUpdateNote}
        noteToEdit={selectedNote}
        onCancel={() => setSelectedNote(null)}
      />
      <div style={styles.notesContainer}>
        {notes.map((noteItem) => (
          <Note
            key={noteItem.id}
            id={noteItem.id}
            title={noteItem.title}
            content={noteItem.content}
            author={noteItem.author}
            onDelete={deleteNote}
            onEdit={() => editNote(noteItem)}
          />
        ))}
      </div>
    </>
  );

  return (
    <Router>
      <div style={styles.app}>
        {!wasValid ? (
          <AccessDenied />
        ) : !isLoggedIn ? (
          <Login onLogin={handleLogin} />
        ) : (
          <>
            <Header username={currentUser.username} />
            <nav style={styles.nav}>
              <Link to="/" style={styles.navLink}>Notes</Link>
              <Link to="/about" style={styles.navLink}>About</Link>
              <button onClick={() => setIsLoggedIn(false)} style={styles.logoutButton}>LOGOUT</button>
            </nav>
            <Routes>
              <Route path="/" element={<NotesPage />} />
              <Route path="/about" element={<About />} />
            </Routes>
          </>
        )}
        <Footer />
      </div>
    </Router>
  );
}

const styles = {
  app: {
    fontFamily: 'Arial, sans-serif',
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '20px',
  },
  nav: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#4CAF50',
    padding: '10px 20px',
    marginBottom: '20px',
  },
  navLink: {
    color: 'white',
    textDecoration: 'none',
    fontSize: '18px',
    marginRight: '20px',
  },
  logoutButton: {
    backgroundColor: '#f44336',
    color: 'white',
    border: 'none',
    padding: '10px 15px',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  notesContainer: {
    display: 'flex',
    flexWrap: 'wrap',
    gap: '20px',
  },
};

export default App;
