import React, { useState, useEffect } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";
import Login from "./Login";
import AccessDenied from "./AccessDenied";
import UpdateArea from "./UpdateArea";
import axios from "axios";

const API_URL = "http://localhost:3000";

function App() {
  const [notes, setNotes] = useState([]);
  const [users, setUsers] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [wasValid, setWasValid] = useState(true);
  const [selectedNote, setSelectedNote] = useState(null);

  const fetchNotes = async () => {
    try {
      const response = await axios.get(`${API_URL}/api/notes`);
      setNotes(response.data);
    } catch (error) {
      console.error("Error fetching notes:", error);
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await axios.get(`${API_URL}/api/users`);
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  useEffect(() => {
    fetchUsers();
    fetchNotes();
  }, []);

  const handleLogin = (username, password) => {
    const user = users.find(
      (user) => user.username === username && user.password === password
    );
    if (user) {
      setCurrentUser(user);
      setIsLoggedIn(true);
      setWasValid(true);
    } else {
      setIsLoggedIn(false);
      setWasValid(false);
    }
  };

  const addNote = async ({ title, content, author }) => {
    try {
      await axios.post(`${API_URL}/api/notes`, { title, content, author });
      fetchNotes();
    } catch (error) {
      console.error("Error adding note:", error.response?.data || error.message);
    }
  };

  const deleteNote = async (id) => {
    try {
      await axios.delete(`${API_URL}/api/notes/${id}`);
      fetchNotes();
    } catch (error) {
      console.error("Error deleting note:", error);
    }
  };

  const updateNote = async (id, updatedNote) => {
    try {
      await axios.put(`${API_URL}/api/notes/${id}`, updatedNote);
      setSelectedNote(null); // Clear the selected note after update
      fetchNotes();
    } catch (error) {
      console.error("Error updating note:", error);
    }
  };

  const notesContainerStyle = {
    display: "flex",
    flexWrap: "wrap", // Allow notes to wrap to the next line if the screen is too small
    justifyContent: "space-between", // Distribute space between notes
    alignItems: "flex-start", // Align notes at the top
    margin: "20px 0", // Add margin to top and bottom
  };

  return (
    <div>
      {!wasValid ? (
        <AccessDenied />
      ) : !isLoggedIn ? (
        <Login onLogin={handleLogin} />
      ) : (
        <>
          <Header />
          <button onClick={() => setIsLoggedIn(false)}>LOGOUT</button>
          <CreateArea onAdd={addNote} />
          <div style={notesContainerStyle}>
            {notes.map((noteItem) => (
              <Note
                key={noteItem.id}
                id={noteItem.id}
                title={noteItem.title}
                content={noteItem.content}
                author={noteItem.author}
                onDelete={deleteNote}
                onEdit={() => setSelectedNote(noteItem)} // Set the selected note for editing
              />
            ))}
          </div>
          {selectedNote && (
            <UpdateArea
              note={selectedNote}
              onEdit={updateNote}
              onCancel={() => setSelectedNote(null)} // Clear selected note on cancel
            />
          )}
          <Footer />
        </>
      )}
    </div>
  );
}

export default App;
