// App.js

import React, { useState, useEffect } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import NoteForm from "./NoteForm";  // We'll rename CreateArea to NoteForm
import Login from "./Login";
import AccessDenied from "./AccessDenied";
import axios from "axios";

const API_URL = "http://localhost:8000";

function App() {
  const [notes, setNotes] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [wasValid, setWasValid] = useState(true);
  const [selectedNote, setSelectedNote] = useState(null);

  const fetchNotes = async () => {
    try {
      const response = await axios.get(`${API_URL}/api/notes`);
      setNotes(response.data);
    } catch (error) {
      console.error("Error fetching notes:", error);
    }
  };

  useEffect(() => {
    if (isLoggedIn) {
      fetchNotes();
    }
  }, [isLoggedIn]);

  const handleLogin = async (username, password) => {
    try {
      const response = await axios.post(`${API_URL}/api/users/login`, { username, password });
      setCurrentUser(response.data.user);
      setIsLoggedIn(true);
      setWasValid(true);
    } catch (error) {
      console.error("Login failed:", error.response?.data || error.message);
      setIsLoggedIn(false);
      setWasValid(false);
    }
  };

  const addOrUpdateNote = async (noteData) => {
    try {
      console.log("Adding/Updating note:", noteData);
      const response = await axios.post(`${API_URL}/api/notes`, noteData);
      console.log("Server response:", response.data);
      fetchNotes();
      setSelectedNote(null);  // Clear selected note after update
    } catch (error) {
      console.error("Error adding/updating note:", error.response?.data || error.message);
    }
  };

  const deleteNote = async (id) => {
    try {
      await axios.delete(`${API_URL}/api/notes/${id}`);
      fetchNotes();
    } catch (error) {
      console.error("Error deleting note:", error);
    }
  };

  const editNote = (note) => {
    setSelectedNote(note);
  };

  return (
    <div>
      {!wasValid ? (
        <AccessDenied />
      ) : !isLoggedIn ? (
        <Login onLogin={handleLogin} />
      ) : (
        <>
          <Header username={currentUser.username} />
          <button onClick={() => setIsLoggedIn(false)}>LOGOUT</button>
          <NoteForm 
            onSubmit={addOrUpdateNote} 
            noteToEdit={selectedNote}
            onCancel={() => setSelectedNote(null)}
          />
          <div style={{ display: 'flex', flexWrap: 'wrap' }}>
            {notes.map((noteItem) => (
              <Note
                key={noteItem.id}
                id={noteItem.id}
                title={noteItem.title}
                content={noteItem.content}
                author={noteItem.author}
                onDelete={deleteNote}
                onEdit={() => editNote(noteItem)}
              />
            ))}
          </div>
          <Footer />
        </>
      )}
    </div>
  );
}

export default App;
