import React, { useState, useEffect } from "react";

function UpdateArea(props) {
  const [note, setNote] = useState({
    title: "",
    content: "",
    author: "",
  });
  useEffect(() => {
    setNote({
      title: props.note.title,
      content: props.note.content,
      author: props.note.author,
    });
  }, []);
  function handleChange(event) {
    const { name, value } = event.target;
    console.log(name, value);
    setNote((prevNote) => {
      return {
        ...prevNote,
        [name]: value,
      };
    });
  }
  function submitNote(event) {
    props.onEdit(props.note.id, note.title, note.content, note.author);
    setNote({
      title: "",
      content: "",
      author: "",
    });
    event.preventDefault();
  }
  return (
    <div>
      <form>
        <input
          name="title"
          onChange={handleChange}
          value={note.title}
          placeholder="Title"
        />
        <textarea
          name="content"
          onChange={handleChange}
          value={note.content}
          placeholder="Take a note..."
          rows="3"
        />
        <input
          name="author"
          onChange={handleChange}
          value={note.author}
          placeholder="Author"
        />
        <button onClick={submitNote}>Update</button>
      </form>
    </div>
  );
}

export default UpdateArea;
