import React, { useState, useEffect } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";
import Login from "./Login";
import AccessDenied from "./AccessDenied";
import UpdateArea from "./UpdateArea";
import axios from "axios";

const API_URL = "http://localhost:8000";

function App() {
  const [notes, setNotes] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [wasValid, setWasValid] = useState(true);
  const [selectedNote, setSelectedNote] = useState(null);

  const fetchNotes = async () => {
    try {
      const response = await axios.get(`${API_URL}/api/notes`);
      setNotes(response.data);
    } catch (error) {
      console.error("Error fetching notes:", error);
    }
  };

  useEffect(() => {
    if (isLoggedIn) {
      fetchNotes();
    }
  }, [isLoggedIn]);

  const handleLogin = async (username, password) => {
    try {
      const response = await axios.post(`${API_URL}/api/users/login`, { username, password });
      setCurrentUser(response.data.user);
      setIsLoggedIn(true);
      setWasValid(true);
    } catch (error) {
      console.error("Login failed:", error.response?.data || error.message);
      setIsLoggedIn(false);
      setWasValid(false);
    }
  };

  const addNote = async (noteData) => {
    try {
      const response = await axios.post(`${API_URL}/api/notes`, noteData);
      console.log("Note added:", response.data);
      fetchNotes();
    } catch (error) {
      console.error("Error adding note:", error.response?.data || error.message);
    }
  };

  const deleteNote = async (id) => {
    try {
      await axios.delete(`${API_URL}/api/notes/${id}`);
      fetchNotes();
    } catch (error) {
      console.error("Error deleting note:", error);
    }
  };

  const updateNote = async (id, updatedNote) => {
    try {
      const response = await axios.put(`${API_URL}/api/notes/${id}`, updatedNote);
      console.log("Note updated:", response.data);
      setSelectedNote(null);
      fetchNotes();
    } catch (error) {
      console.error("Error updating note:", error.response?.data || error.message);
    }
  };

  const editNote = (note) => {
    setSelectedNote(note);
  };

  const notesContainerStyle = {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "space-between",
    alignItems: "flex-start",
    margin: "20px 0",
  };

  return (
    <div>
      {!wasValid ? (
        <AccessDenied />
      ) : !isLoggedIn ? (
        <Login onLogin={handleLogin} />
      ) : (
        <>
          <Header username={currentUser.username} />
          <button onClick={() => setIsLoggedIn(false)}>LOGOUT</button>
          <CreateArea onAdd={addNote} />
          <div style={notesContainerStyle}>
            {notes.map((noteItem) => (
              <Note
                key={noteItem.id}
                id={noteItem.id}
                title={noteItem.title}
                content={noteItem.content}
                author={noteItem.author}
                onDelete={deleteNote}
                onEdit={() => editNote(noteItem)}
              />
            ))}
          </div>
          {selectedNote && (
            <UpdateArea
              note={selectedNote}
              onEdit={updateNote}
              onCancel={() => setSelectedNote(null)}
            />
          )}
          <Footer />
        </>
      )}
    </div>
  );
}

export default App;
