import React from "react";

function Note({ id, title, content, author, onDelete, onEdit }) {
  const noteStyle = {
    border: "1px solid #ddd",
    borderRadius: "8px",
    padding: "16px",
    margin: "16px 0", // Adjusted to only have vertical margins
    backgroundColor: "#f9f9f9",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
    maxWidth: "300px",
    width: "100%", // Ensures the note takes the full width of its container
  };

  const buttonStyle = {
    margin: "8px 4px", // Adjusted for better spacing between buttons
    padding: "8px 16px",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  };

  const deleteButtonStyle = {
    ...buttonStyle,
    backgroundColor: "#f44336",
    color: "white",
  };

  const editButtonStyle = {
    ...buttonStyle,
    backgroundColor: "#4CAF50",
    color: "white",
  };

  return (
    <div style={noteStyle}>
      <h1 style={{ fontSize: "1.5rem", margin: "0 0 8px" }}>{title}</h1> {/* Title styling */}
      <p style={{ margin: "0 0 8px" }}>{content}</p> {/* Content styling */}
      <p style={{ fontStyle: "italic", margin: "0 0 16px" }}>{author}</p> {/* Author styling */}
      <button style={deleteButtonStyle} onClick={() => onDelete(id)}>
        DELETE
      </button>
      <button style={editButtonStyle} onClick={() => onEdit(id)}>
        EDIT
      </button>
    </div>
  );
}

export default Note;
